import React from "react";

import { Link } from "react-router-dom";
import { useState , useEffect} from "react";
import ManagerProject from "./ManagerProject";
import { useParams } from "react-router-dom";
import EmployeesList from "./EmployeesList";
import axios from "axios";

const ManagerPage = () =>{
    const {managerName} = useParams();
    const[showManagerProject,setShowManagerProject] = useState(false);
    const[showEmployeeTable, setShowEmployeeTable] = useState(false);
    const[employees,setEmployees] = useState([]);
    const[projects,setProjects] =useState([]);
     
    console.log("Manager name:" ,managerName);

    useEffect(() =>{
        const fetchProjectDetails = async() =>{
         try{
             const response = await axios.get(`http://localhost:8080/admin/getProject/${managerName}`);
             console.log(response.data);
             setProjects([response.data]);
         }
         catch(error){
             console.log("Error fetching project details:", error);
         }
        }
        fetchProjectDetails();
     },[managerName]);

    
     useEffect(()=>{
        const fetchEmployees = async() =>{
            try{
                const response = await axios.get(`http://localhost:8080/manager/getEmployees`);
                console.log("Fetched employees successfully: ", response.data);
                setEmployees(response.data);
                
            }
            catch(error){
                console.log("Error fetching employees: ",error);
            }
        }
        fetchEmployees();
    },[]);
    
    
    const handleAddClick = () =>{
        navigate(`/registerEmployee`);
    }

   return(
    <React.Fragment>
         <div className="navbar">
            <Link to="/" className="links">Task Management System</Link>
           
            <a href="#about" className="links">About Us</a>
           
            <a href="#contact" className="links">Contact Us</a>
            <div className="navbarTwo">
            <Link to ={`/myProjects/${managerName}`} className ="links"  onClick = {() =>{setShowManagerProject(true); setShowEmployeeTable(false)}}>My Projects</Link>
            <Link to="/registerEmployee" className ="links">Register Employee</Link>
            <Link to ="/viewAllEmployeesInManager" className="links" onClick = {()=>{setShowEmployeeTable(true); setShowManagerProject(false)}} >View All Employees</Link>
            <Link to ="/changePassword" className="links">Change Password</Link>
            <Link to="/logout" className="links">Logout</Link>
            </div>
        </div>

        {showManagerProject && (
            <table className="table">
            <thead className="tableHeading">
            <tr>
               <th>ProjectId</th>
                <th>Project Name</th>
                <th>Project Description</th>
                <th>Project Requirement</th>
                <th>Manager Assign Status</th>
                <th>Manager Name</th>
                <th>Employee Assign Status</th>
                <th>Employee Name</th>
                <th>Porject Created Date</th>
                <th>Project Assign Date</th>
                <th>Project Deadline</th>
                <th>Project Status</th>
                <th>Action</th>
            </tr>
            </thead>
            <tbody className="tableBody">
                {projects.map((project,index)=>(
                    <tr key={project.id} className="tableRow">
                       <td>{project.id}</td>
                       <td>{project.name}</td>
                        <td>{project.description}</td>
                        <td>{project.requirement}</td>
                        <td>{project.managerAssignStatus}</td>
                        <td>{project.managerName}</td>
                        <td>{project.employeeAssignStatus}</td>
                        <td>{project.employeeName}</td>
                        <td>{project.projectCreatedDate}</td>
                        <td>{project.projectAssignDate}</td>
                        <td>{project.deadline}</td>
                        <td>{project.projectStatus}</td>
                        <td><button className="btnPro">Assign To Employee</button></td>
                        
                    </tr>
                    
                ))}

               
            </tbody>
        </table>
        )}


        {showEmployeeTable &&(
              <div>
              <h1 className="title">All Employees</h1>
              <button className ="btn" onClick = {handleAddClick}>Add Employee</button>
          
      
              <table className="table">
                  <thead className="tableHeading">
                  <tr>
                      <th>FirstName</th>
                      <th>LastName</th>
                      <th>EmailId</th>
                      <th>PhoneNo</th>
                      <th>Address</th>
                      <th>Actions</th>
                  </tr>
                  </thead>
                  <tbody className="tableBody">
                      {employees.map((employee,index)=>(
                          <tr key={employee.emailId} className="tableRow">
                              <td>{employee.firstName}</td>
                              <td>{employee.lastName}</td>
                              <td>{employee.emailId}</td>
                              <td>{employee.contactNo}</td>
                              <td>{employee.street}, {employee.city} - {employee.pincode}</td>
                              <td><button className="btn" >Remove</button></td>
                          </tr>
                          
                      ))}
      
                     
                  </tbody>
              </table>
              </div>
        )}

       

        
    </React.Fragment>
   )

}

export default ManagerPage;
