import React from "react";
import { useState,useEffect } from "react";
import axios from "axios";
const initialState = {
    
    role: "",
    emailId: "",
    password: ""
  };
const Login = () =>{
    const[role,setRole] = useState('');
    const[user,setUser]= useState(initialState);

    const handleInputChange = (e) =>{
        const{name,value} = e.target;
        setUser({...user,[name]:value});

    }
   const handleRoleChange = (e) =>{
    setRole(e.target.value);
   }

    const handleSubmit = async(e)=>{
        e.preventDefault();
        try{
            if(role === "Admin"){
                await axios.get(`http://localhost:8080/admin/getUser/${user.emailId}`);
                console.log("User found successfully")

            }
            else if(role === "Manager"){
                await axios.fetch(`http://localhost:8080/manager/getUser/${user.emailId}`);
                console.log("Manager found succesfully");
            }
        }
        catch(error){
            console.log("Error finding User: ",error);
        }

    }

    return(
        <React.Fragment>
            <div className="form1">
                <body>
                <h1 className="title1">Login</h1>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="role" className="label1">Role</label>
                    <select 
                        value={user.gender} 
                        id="role" 
                        name="role"
                        onChange={e => {
                            handleInputChange(e);
                            handleRoleChange(e);
                        }}
                        className="input1"
                    >
                        <option  value="" selected disabled >Role</option>
                        <option value="Admin">Admin</option>
                        <option value="Manager">Manager</option>
                        <option value="Employee">Employee</option>
                    </select>

                    <label htmlFor="emailId" className="label1">EmailId</label>
                    <input 
                        className="input2"
                        id ="emailId"
                        name="emailId"
                        value={user.emailId}
                        
                        onChange = {handleInputChange}
                    ></input>


                    <label htmlFor="password" className="label1">Password</label>
                    <input
                        className="input3"
                        type="password"
                        id="password"
                        name="password"
                        value={user.password}
                        onChange={handleInputChange}
                    >
                    </input>

                    <button className="btn1">Login</button>

                </form>
                </body>
            </div>
        </React.Fragment>
    )

}

export default Login;
