import React from "react";
import { useState } from "react";

const initialState ={
    name:'',
    description:'',
    requirement:'',
    deadline:''
}
const AddProject = () =>{
    const[project,setProject] = useState(initialState);
    const handleInputChange = (e) =>{
        const{name,value} = e.target;
        setProject({...project,[name]:value});
    }

    const handleSubmit = async(e) =>{
        e.preventDefault();
        try{

        }
        catch(error){
            console.log("Error adding project: ")
        }
    }
    return(
        <React.Fragment>
            <div className="formPro">
                <body >
                    <form  onSubmit={handleSubmit}>
                        <h1 className="title">Add Project</h1>
                        

                        <label htmlFor="projectName" className="labelPro">ProjectName</label>
                        <input
                            className="inputPro"
                            type="text"
                            id ="projectName"
                            name="projectName"
                            value={project.name}
                            onChange = {handleInputChange}
                        >
                        </input>

                        <label htmlFor="projectDescription" className="labelPro">
                            ProjectDescription
                            </label>
                        <textarea
                            className="inputNarrowPro"
                            id="projectDescription"
                            name="projectDescription"
                            value={project.description}
                            onChange={handleInputChange}
                        ></textarea>

                        <label htmlFor="projectRequirement" className="labelPro">
                            ProjectRequirement
                            </label>
                        <textarea
                            className="inputNarrowPro"
                            id="projectRequirement"
                            name="projectRequirement"
                            value={project.requirement}
                            onChange={handleInputChange}
                        ></textarea>

                        <label htmlFor="projectDeadline" className="labelPro">ProjectDeadline</label>
                        <input
                            className="inputPro"
                            type="date"
                            id="projectDeadline"
                            name="projectDeadline"
                            value={project.deadline}
                            onChange={handleInputChange}
                        >

                        </input>

                        <button className="btn">Add Project</button>
                        

                    </form>
                </body>
            </div>
        </React.Fragment>
    )
}

export default AddProject;
