import React from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { useState,useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const initialState={
    firstName: "",
  lastName: "",
  role: "",
  emailId: "",
  password: "",
  gender: "",
  contactNo: "",
  age: "",
  street: "",
  city: "",
  pincode: ""
}


const AdminPage = () =>{
    const navigate = useNavigate();
    //const{name} = useParams();
    const[showManagerTable, setShowManagerTable] = useState(false)
    const[managers,setManagers] = useState([]);

    const handleClick = (e)=>{
        e.preventDefault();
        navigate(`/managerRegister`);
    }

    const hanldeRemoveClick = async(e,emailId)=>{
        e.preventDefault();
        try{
            await axios.delete(`http://localhost:8080/admin/deleteManager/${emailId}`);
            console.log("Removed manager successfully");

            

        }
        catch(error){
            console.log("Error removing manager:",error);
        }
    }
    useEffect(()=>{
        const fetchManagers = async() =>{
            try{
                const response = await axios.get(`http://localhost:8080/manager/getManagers`);
                const data= response.data;
                setManagers(data);
                
            }
            catch(error){
                console.log("Error fetching managers: ",error);
            }

        }
        fetchManagers();
    },[]);
    return(
        <React.Fragment>
         <div className="navbar">
            <a href="#Home" className="links">Task Management System</a>
           
            <a href="#about" className="links">About</a>
           
            <a href="#contact" className="links">Contact</a>
       
            <Link to ="/managerRegister" className ="links">RegisterManager</Link>
            <Link to ="/addProject" className ="links">AddProject</Link>
            <Link to ="/allProjects" className ="links">AllProjects</Link>
            <Link to ="/viewManagers" className ="links">ViewManager</Link>
            <Link to ="/viewAllEmployees" className ="links">ViewAllEmployees</Link>
            <Link to="/changePassword" className="links">ChangPassword</Link>
            <Link to="/logout" className="links">Logout</Link>
        </div>
           
        </React.Fragment>
    )
}

export default AdminPage;
