import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const AssignProjectToManager = () => {
    const { projectDetails } = useParams();
    const navigate = useNavigate();
    const [project, setProject] = useState([]);

    useEffect(() => {
        if (projectDetails) {
            try {
                const response = JSON.parse(projectDetails);
                setProject(response);
            } catch (error) {
                console.log("Error parsing details", error);
            }
        }
    }, [projectDetails]);

    const handleClick = () => {
        navigate(`/adminPage`);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setProject({ ...project, [name]: value });
    };

    return (
        <React.Fragment>
            <div className="formPro">
                <body>
                    <form>
                        <h1 className="title">Assign Project To Manager</h1>

                        <label htmlFor="name" className="labelPro">
                            Project Name
                        </label>
                        <input
                            className="inputPro"
                            type="text"
                            id="name"
                            name="name"
                            value={project.name || ""}
                            readOnly
                        />

                        <label htmlFor="description" className="labelPro">
                            Project Description
                        </label>
                        <textarea
                            className="inputNarrowPro"
                            id="description"
                            name="description"
                            value={project.description || ""}
                            readOnly
                        ></textarea>

                        <label htmlFor="requirement" className="labelPro">
                            Project Requirement
                        </label>
                        <textarea
                            className="inputNarrowPro"
                            id="requirement"
                            name="requirement"
                            value={project.requirement || ""}
                            readOnly
                        ></textarea>

                        <label htmlFor="createdDate" className="labelPro">
                            Project Created Date
                        </label>
                        <input
                            className="inputPro"
                            type="date"
                            id="createdDate"
                            name="createdDate"
                            value={project.createdDate || ""}
                            readOnly
                        />

                        <label htmlFor="deadline" className="labelPro">
                            Project Deadline
                        </label>
                        <input
                            className="inputPro"
                            type="date"
                            id="deadline"
                            name="deadline"
                            value={project.deadline || ""}
                            readOnly
                        />
                        <label htmlFor="assignProjectToManager" className="labelPro">
                            Assign Project To Manager
                        </label>
                        <input
                            className="inputPro"
                            type="text"
                            id="assignProjectToManager"
                            name="assignProjectToManager"
                            value={project.managerName || ""}
                            onChange={handleInputChange}
                        />

                        <button className="btnPro" disabled>
                            Add Project (Disabled)
                        </button>
                        <button className="btnPro" onClick={handleClick}>
                            Back to Admin Page
                        </button>
                    </form>
                </body>
            </div>
        </React.Fragment>
    );
};

export default AssignProjectToManager;
