import React from "react";
import { useState,useEffect } from "react";
const initialState = {
    firstName: "",
    lastName: "",
    role: "",
    emailId: "",
    password: "",
    gender: "",
    contactNo: "",
    age: "",
    street: "",
    city: "",
    pincode: ""
  };
const Login = () =>{
    const[user,setUser]= useState(initialState);

    return(
        <React.Fragment>
            <div className="form">
                <h1 className="title">Login</h1>
                <form>
                    <label htmlFor="role" className="label">Role</label>
                    <select 
                        value={user.gender} 
                        id="role" 
                        name="role"
                        onChange ={hanldeInputChange}
                    >
                        <option>disabled select</option>
                        <option>Admin</option>
                        <option>Manager</option>
                        <option>Employee</option>
                    </select>

                    <label htmlFor="emailId" className="label">EmailId</label>
                    <input 
                        className="input"
                        id ="emailId"
                        name="emailId"
                        value={user.emailId}
                        onChange = {hanldeInputChange}
                    ></input>

                </form>
            </div>
        </React.Fragment>
    )

}

export default Login;
