import React from "react";
import { useState,useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const ChangePassword = () =>{
    const{managerName} = useParams();
    const[manager,setManager] = useState([]);

    useEffect(()=>{
        const fetchManager = async() =>{
            try{
                const response = await axios.get(`http://localhost:8080/getManager/${managerName}`);
                console.log("Fetched manager Successfully", response.data);
                setManager(response.data);
            }
            catch(error){
                console.log("Error fetching manager: ", error);
            }
        }  
        fetchManager(); 
    },[managerName]);

    const handleInputChange = (e) =>{
        setManager({...manager,[e.target.name]:e.target.value})
    }

    const handleSubmit = async(e) =>{
        e.preventDefault()
        try{
            const response = await axios.post(`http://localhost:8080/manager/upatePassword/${manager.id}`,manager);
            console.log("Password updated successfully", response.data);

        }
        catch(error){
            console.log("Error updating password", error);
        }
    }
    return(
        <React.Fragment>
            <div className="formPassword">
                <body>

                    <form onSubmit ={handleSubmit}>
                        <h1 className="titlePass">Change Password</h1>
                        <label htmlFor="emailId" className="labelPass">EmailId</label>
                        <input
                            className="inputPass"
                            type="text"
                            id ="emailId"
                            name ="emailId"
                            value ={manager.emailId}
                            readOnly
                        >
                        </input>


                        <label htmlFor="contactNo" className="labelPass">ContactNo</label>
                        <input
                            className="inputPass"
                            type="text"
                            id="contactNo"
                            name ="contactNo"
                            value={manager.contactNo}
                            readOnly
                        >
                        </input>

                        <label htmlFor="password" className="labelPass">Password</label>
                        <input
                            className="inputPass"
                            type="password"
                            id="password"
                            name="password"
                            value={manager.password}
                            onChange = {handleInputChange}

                        >
                        </input>
                        <button className="btnPass" >Change Password</button>
                    </form>
                </body>
            </div>
        </React.Fragment>
    )
}
export default ChangePassword;
