import React from "react";
import { useState } from "react";

const initialState ={
    name:'',
    description:'',
    requirement:'',
    deadline:''
}
const AddProject = () =>{
    const[project,setProject] = useState(initialState);
    const handleInputChange = (e) =>{
        const{name,value} = e.target;
        setProject({...project,[name]:value});
    }
    return(
        <React.Fragment>
            <div className="form">
                <body>
                    <form>
                        <h1 className="title">Add Project</h1>
                        

                        <label htmlFor="projectName" className="label">ProjectName</label>
                        <input
                            className="input"
                            type="text"
                            id ="projectName"
                            name="projectName"
                            value={project.name}
                            onChange = {handleInputChange}
                        >
                        </input>

                        <label htmlFor="projectDescription" className="label">
                            Street
                            </label>
                        <textarea
                            className="input"
                            id="street"
                            name="projectDescription"
                            value={admin.street}
                            onChange={handleInputChange}
                        ></textarea>

                    </form>
                </body>
            </div>
        </React.Fragment>
    )
}

export default AddProject;
