import React from "react";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const AssignProjectToManager = () =>{
    const navigate = useNavigate();
    const[project,setProject]= useState([]);
    const handleClick = () =>{
        navigate(`/adminPage`);
    }
    useEffect(()=>{
        const fetchProjectDetails = async() =>{
            try{
                const response = await axios.get(`http://localhost:8080/admin/getProject`);
                setProject(response.data);
            }
            catch(error){
                console.log("Error fetching details",error);
            }
        }
        fetchProjectDetails();
    },[]);
    const handleInputChange  = (e) =>{
        const{name,value} = e.target;
        setProject({...project,[name]:value});
    }
   

    return(
        <React.Fragment>
            <div className="formPro">
        <body>
          <form >
            <h1 className="title">Add Project</h1>

            <label htmlFor="name" className="labelPro">
              ProjectName
            </label>
            <input
              className="inputPro"
              type="text"
              id="name"
              name="name"
              value={project.name}
              readOnly
             
            />

            <label htmlFor="description" className="labelPro">
              ProjectDescription
            </label>
            <textarea
              className="inputNarrowPro"
              id="description"
              name="description"
              value={project.description}
              readOnly
             
            ></textarea>

            <label htmlFor="requirement" className="labelPro">
              ProjectRequirement
            </label>
            <textarea
              className="inputNarrowPro"
              id="requirement"
              name="requirement"
              value={project.requirement}
              readOnly
              
            ></textarea>

            <label htmlFor="createdDate" className="labelPro">projectCreatedDate</label>
            <input
              className="inputPro"
              type="date"
              id="deadline"
              name="deadline"
              value={project.deadline}
              readOnly
              
            />

            <label htmlFor="deadline" className="labelPro">
              ProjectDeadline
            </label>
            <input
              className="inputPro"
              type="date"
              id="deadline"
              name="deadline"
              value={project.deadline}
              readOnly
             
            />
            <label htmlFor="assignProjectToManager" className="labelPro">AssignProjectToManager</label>
            <input
                className="inputPro"
                type="text"
                id="assignProjectToManager"
                name="assignProjectToManager"
                value={project.managerName}
                onChange = {handleInputChange}

            >
            </input>

            <button className="btnPro">Add Project</button>
            <button className="btnPro" onClick={handleClick}>AdminPage</button>
          </form>
        </body>
      </div>
        </React.Fragment>
    )

}

export default AssignProjectToManager;
