import React from "react";
import { useState , useEffect} from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const initialState = {
    id: "",
    name: "",
    description: "",
    requirement: "",
    managerAssignStatus: "",
    managerName: "",
    employeeAssignStatus: "",
    employeeName: "",
    projectCreatedDate: "",
    projectAssignDate: "",
    deadline: "",
    projectStatus: ""
};


const ManagerProjectTable = (props) =>{
   
    
    const{managerName} = props;
    console.log(managerName);

    const [projects, setProjects] = useState([initialState]);

    useEffect(() => {
        const fetchManagerProjects = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/admin/getProjectByName`);
                console.log("Fetched Manager Projects Successfully", response.data);
              
                const filteredProjects = response.data.filter(project => managerName ===  project.managerName);

                setProjects(filter);
            } catch (error) {
                console.error("Error fetching manager projects: ", error);
            }
        }

        fetchManagerProjects();
    }, [managerName]);
    return(
        <React.Fragment>
           
           
           
             <table className="table">
                 <thead className="tableHeading">
                 <tr>
                    <th>ProjectId</th>
                     <th>Project Name</th>
                     <th>Project Description</th>
                     <th>Project Requirement</th>
                     <th>Manager Assign Status</th>
                     <th>Manager Name</th>
                     <th>Employee Assign Status</th>
                     <th>Employee Name</th>
                     <th>Porject Created Date</th>
                     <th>Project Assign Date</th>
                     <th>Project Deadline</th>
                     <th>Project Status</th>
                     <th>Action</th>
                 </tr>
                 </thead>
                 <tbody className="tableBody">
                     
                 {projects.map((project, index) => (
                    <React.Fragment key={project.id}>
                      
                    
                                <tr className="tableRow">
                                    <td>{project.id}</td>
                                    <td>{project.name}</td>
                                    <td>{project.description}</td>
                                    <td>{project.requirement}</td>
                                    <td>{project.managerAssignStatus}</td>
                                    <td>{project.managerName}</td>
                                    <td>{project.employeeAssignStatus}</td>
                                    <td>{project.employeeName}</td>
                                    <td>{project.projectCreatedDate}</td>
                                    <td>{project.projectAssignDate}</td>
                                    <td>{project.deadline}</td>
                                    <td>{project.projectStatus}</td>
                                    <td></td>
                                </tr>
                            
                        
                    </React.Fragment>
))}


                    
                 </tbody>
             </table>
              
        </React.Fragment>
    )
}

export default ManagerProjectTable;
